mapper.insert:
INSERT INTO orders(
		goods_no
		, orders_amount
		, payment_no
	) VALUES (
			#{goodsNo}
			, #{ordersAmount}
			, #{paymentNo}
	)

mapper.update:
UPDATE payment p
INNER JOIN orders o 
ON p.payment_no = o.payment_no
SET 
	p.payment_state = #{paymentState}
WHERE o.orders_no = #{ordersNo};
